// frontend/src/components/VideoDownloader.js

import React, { useState } from 'react';
import axios from 'axios';

const VideoDownloader = () => {
    const [url, setUrl] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleDownload = async () => {
        if (!url) {
            setError('Please enter a URL');
            return;
        }

        setError('');
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:5000/api/download', { url }, { responseType: 'blob' });

            const contentType = response.headers['content-type'];

            if (contentType && contentType.startsWith('text/html')) {
                setError('The URL returned HTML instead of a video.');
                return;
            }

            const blob = new Blob([response.data], { type: contentType });
            const link = document.createElement('a');
            link.href = window.URL.createObjectURL(blob);
            link.download = 'video.mp4';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        } catch (err) {
            console.error('Error during download:', err);
            setError('Error downloading the video');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <input
                type="text"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="Enter video URL"
            />
            <button onClick={handleDownload} disabled={loading}>
                {loading ? 'Downloading...' : 'Download Video'}
            </button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default VideoDownloader;
